import React, { useEffect, useState } from 'react';
import gsap from 'gsap'; // Importer GSAP
import './style.css'; // Inclure le fichier CSS mis à jour

const MoroccanArtisanPage = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Lancer l'animation du cercle de chargement
    gsap.to('.circle', {
      rotation: 360 * 4, // 4 tours
      strokeDasharray: "440", // Valeur max pour le cercle
      strokeDashoffset: "0", // Commence à se remplir
      duration: 3,
      ease: "power1.inOut",
      onComplete: () => {
        // Lorsque l'animation est terminée, cacher l'écran de chargement
        setIsLoading(false);
      },
    });
  }, []);

  return (
    <div className="container">
      {isLoading && (
        <div id="loading-screen">
          <svg className="circle" viewBox="0 0 120 120">
            <circle cx="60" cy="60" r="55" stroke="white" strokeWidth="5" fill="none" />
          </svg>
        </div>
      )}

      {/* Contenu de la page */}
      {!isLoading && (
        <>
          {/* Navbar */}
          <nav className="navbar">
            <div className="nav-links">
              <a href="#home">Home</a>
              <a href="#shop">Shop</a>
              <a href="#artisans">Artisans</a>
              <a href="#about">About</a>
              <a href="#contact">Contact</a>
            </div>
            <div className="auth-buttons">
              <button className="login-button">Log In</button>
              <button className="signup-button">Sign Up</button>
            </div>
          </nav>

          {/* Hero Section */}
          <div className="hero-section">
            <h2>Tradition Meets Innovation</h2>
            <h1>Discover Moroccan <br /> Artisan Excellence</h1>
            <button className="cta-button">Learn About Artisan</button>
          </div>
        </>
      )}
    </div>
  );
};

export default MoroccanArtisanPage;
