import React, { useEffect } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger"; // Import ScrollTrigger
import "./AboutUS.css";

gsap.registerPlugin(ScrollTrigger); // Enregistrer le plugin

const testimonials = [
  {
    name: "HAchim",
    text: "A simple and smart way to connect artisans with marketers.",
  },
  {
    name: "Youssef ",
    text: "Une plateforme innovante pour promouvoir le Made In Morocco dans le monde.",
  },
  {
    name: "Manall",
    text: "Nice Website for linking the Moroccan Artisanal.",
  },
  {
    name: "Brahim ait mhamed",
    text: "A promising concept to showcase Moroccan craftsmanship. But you still have small problems.",
  },
];

const Testimonials = () => {
  useEffect(() => {
    // Animation des cartes de témoignages
    gsap.from(".testimonial-card", {
      opacity: 0,
      y: 50,
      stagger: 0.2, // Espacement entre les animations des cartes
      duration: 1,
      scrollTrigger: {
        trigger: ".testimonials-section", // Le déclencheur est la section des témoignages
        start: "top 8%", // Commence l'animation quand l'élément arrive à 80% de la fenêtre
        end: "top 30%", // Termine l'animation quand il atteint 30% de la fenêtre
        scrub: true, // Animation fluide au défilement
        once: true, // Animation ne se déclenche qu'une fois
      }
    });
  }, []);

  return (
    <div className="testimonials-section">
      <h2 className="section-title">What People Are Saying About Artizal</h2>
      <div className="testimonials-grid">
        {testimonials.map((testimonial, index) => (
          <div key={index} className="testimonial-card">
            <div className="testimonial-icon">★</div>
            <p className="testimonial-name">{testimonial.name}</p>
            <p className="testimonial-text">{testimonial.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Testimonials;
